#include <iostream>
#include <vector>
#include <math.h>
#include <string>
#include <stdlib.h>

using namespace std;

int main(int argc, char* argv[])
{
	string number = "";


	unsigned int digits[5];
	for(unsigned int j=0; j<5; j++)
	{
		char temp = number.at(j); 
		digits[j] = atoi(&temp);
	}

	unsigned int max = digits[0]*digits[1]*digits[2]*digits[3]*digits[4];
	

	for(unsigned int i=5; i<1000; i++)
	{
		for(unsigned int j=0; j<4; j++)
		{
			digits[j] = digits[j+1];
		}
		char temp = number.at(i); 
		digits[4] = atoi(&temp);

		unsigned int mult = digits[0]*digits[1]*digits[2]*digits[3]*digits[4];
		if( mult > max )
			max = mult;

	}


	cout << max << endl;


	return 0;
}
